
public class Operacoes {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		Integer num = 100;
		System.out.println(num + 50);
		System.out.println(num - 50);
		System.out.println(num / 50);
		System.out.println(num * 5);
		System.out.println(num % 3);
		System.out.println(num++);
		System.out.println(++num);
	}
}
